  // setTodoList(newTodoList)
  setTodoList(todoList.filter((t)=> t !== taskName))

  }

  return (
    <div className="App">
      <p>Hollla Amigo</p>
      <div className="createTask">
        <input onChange={
          (event)=>{
            setNewTask(event.target.value)
          }
        }/>
        <button onClick={addTask}> add task </button>

      </div>
      <div className="taskList">
        {todoList.map((task)=>{
          return (
            <div> 
              <h4>{task}</h4>
